styles:
ul li {
    display:inline;
}

.maincontent {
    position: relative;
    width: 100%; /* for IE 6 */
    height: 65vh;
    width: 100%;
    background-size: 100% 100%;
    background-repeat: no-repeat;
}
.maincontent div {
    position: absolute;
    top: 10px;
    left: 0;
    width: 100%;
}

.title{
    width: 100%;
    font-size: 180%;
    font-weight: 700;
    line-height: 130%;
    color: #fff;
    text-shadow: 2px 4px 3px rgba(0,0,0,0.3);
}
@media screen and (min-width: 1400px){
    .maincontent div {
        height: 32vw;
        padding-bottom: 20px;
    }
}
@media (max-width: 799px)
{
    .maincontent div {
        font-size: 11px;
    }

    .maincontent div {
        top: 10px;
        width: 33vw;
        min-width: 330px;
        color: #999;
        line-height: 1.3;
    }
    .maincontent div table {
        height: 50px;
    }
}
@media(max-width: 480px) {

    img{
        width: 170px;
        height: 35px;
    }
    .title{
        width: 100%;
        font-size: 180%;
        font-weight: 700;
        line-height: 130%;
        color: #fff;
        text-shadow: 2px 4px 3px rgba(0,0,0,0.3);
    }
    h3 {
        font-size: 9pt;
    }
    span{
        font-size: 6pt;
    }
    li b{
        font-size: 6pt;
    }
    li {
        font-size: 6pt;
    }
    .maincontent {
        position: relative;
        width: 100%; /* for IE 6 */
        height: 35vh;
        width: 100%;
        background-size: 100% 100%;
        background-repeat: no-repeat;
    }
}
header{
    width: 100%;
    padding: 0px 48px;
    background-image: linear-gradient(rgba(0, 0, 0, 0.0), #141414);
    background-size: cover;
    background-position: center;
    height: 100vh;
}
header .billboard{
    margin-top: 40px;
    width: 40%;
    padding: 2px;
    font-size: 110%;
    line-height: 160%;
}
header .billboard .tab-area a{
    width: 120px;
    float: left;
    font-size: 90%;
    color: #ffffff;
    text-align: center;
    text-transform: uppercase;
    text-decoration: none;
    padding: 8px;
    margin-right: .75em;
    margin-bottom: 30px;
    transition: border 0.5s;
}
header .billboard .tab-area a:hover{
    border-bottom: solid 2px #eeeeee;
}
header .billboard .tab-area .active{
    border-bottom: solid 2px #e50914;
}
/* Text Related CSS */
header .billboard .content-area{
    color: #999;
    font-size: 100%;
}
header .billboard .content-area .subtitle{
    font-size: 120%;
    line-height: 200%;
}
header .billboard .content-area .credits{
    font-size: 80%;
}
header .billboard .content-area .credits strong{
    color: #fff;
}
/* Button Related CSS */
header .billboard .button{
    display: inline-block;
    margin-right: .75em;
    margin-top: 20px;
    padding: 5px 20px;
    font-size: 70%;
    font-weight: 500;
    text-decoration: none;
    text-transform: uppercase;
    color: #fff;
}
.button i{
    display: inline-block;
    margin-top: 20px;
    width: 40px;
    height: 40px;
    padding: 10px 0 0 11px;
    border: solid 2px rgba(255,255,255,.4);
    border-radius: 100px;
    font-size: 18px;
    font-weight: 500;
    text-decoration: none;
    text-transform: uppercase;
    color: #fff;
    transition: transform 0.5s;
}
.button i:hover{
    border: solid 2px #fff;
    transform: scale(1.1);
}

.buttonSmall i{
    display: inline-block;
    width: 25px;
    height: 25px;
    padding: 7px 0 0 5px;
    border: solid 2px rgba(255,255,255,.4);
    border-radius: 100px;
    font-size: 12px;
    font-weight: 500;
    text-decoration: none;
    text-transform: uppercase;
    color: #fff;
    transition: transform 0.5s;
}
.buttonSmall i:hover{
    border: solid 2px #fff;
    transform: scale(1.1);
}

.close-button{
    cursor: pointer;
    padding-left: 98%;color:gray;background: radial-gradient(ellipse at top right,rgba(0,0,0,.4) 0,rgba(0,0,0,0)70%,rgba(0,0,0,0) 100%);
}

  .glyphicon-refresh-animate {
    -animation: spin .7s infinite linear;
    -webkit-animation: spin2 .7s infinite linear;
}

@-webkit-keyframes spin2 {
    from { -webkit-transform: rotate(0deg);}
    to { -webkit-transform: rotate(360deg);}
}

@keyframes spin {
    from { transform: scale(1) rotate(0deg);}
    to { transform: scale(1) rotate(360deg);}
}

/**
 A Style Sheet for all form inputs common used classes
 */

      /** Form Validations & Icon Positioning **/
      .has-feedback-custom {
          position: relative;
      }
      .has-feedback-custom .form-control {
          padding-right: 47.5px;
      }

      .form-control-feedback-custom {
          position: absolute;
          top: 0;
          right: 0;
          z-index: 2;
          display: block;
          width: 38px;
          height: 38px;
          line-height: 38px;
          text-align: center;
          pointer-events: none;
      }

      .has-feedback-custom label ~ .form-control-feedback-custom {
          top: 32px;
      }
      .has-feedback-custom label.sr-only ~ .form-control-feedback-custom {
          top: 0;
      }
      .amexio-rating {
          cursor: pointer;
          outline: none;
      }
      .amexio-rating.readonly {
          cursor: default;
      }
      .amexio-rating.disabled {
          cursor: not-allowed;
      }
      .amexio-rating i{
          font-style: normal;
      }
      .amexio-star-icon {
          color: #666666;
          font-size: 2em;
          position: relative;
          border-color: 1px solid black;
      }
      .amexio-star-icon:before {
          color: #FDE16D;
          content: attr(data-icon) " ";
          position: absolute;
          left: 0;
          overflow: hidden;
          width: 0;
      }
      .amexio-rating.disabled .amexio-star-icon:before {
          color: #ECECEC;
          text-shadow: none;
      }
      .amexio-star-icon.half10:before {
          width: 10%;
      }
      .amexio-star-icon.half20:before {
          width: 20%;
      }
      .amexio-star-icon.half30:before {
          width: 30%;
      }
      .amexio-star-icon.half40:before {
          width: 40%;
      }
      .amexio-star-icon.half50:before {
          width: 50%;
      }
      .amexio-star-icon.half60:before {
          width: 60%;
      }
      .amexio-star-icon.half70:before {
          width: 70%;
      }
      .amexio-star-icon.half80:before {
          width: 80%;
      }
      .amexio-star-icon.half90:before {
          width: 90%;
      }
      .amexio-star-icon.half100:before {
          width: 100%;
      }
      @-moz-document url-prefix() { /* Firefox Hack */
          .amexio-star-icon {
              font-size: 50px;
              line-height: 34px;
          }
      }

.close-button{
    cursor: pointer;
    padding-left: 98%;color:gray;background: radial-gradient(ellipse at top right,rgba(0,0,0,.4) 0,rgba(0,0,0,0)70%,rgba(0,0,0,0) 100%);
}
.close-button i:hover{
    color: red;
    cursor: pointer;
}
